import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Image from 'react-bootstrap/Image';
import 'bootstrap/dist/css/bootstrap.min.css';
import lancheirinha from './assents/lancherinha.png';
import { getAllProducts } from './api/apiService'; // Importa a função para buscar produtos

function App() {
  const [products, setProducts] = useState([]); // Estado para armazenar os produtos

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const data = await getAllProducts(); // Busca produtos da API
        setProducts(data); // Atualiza o estado com os produtos recebidos
      } catch (error) {
        console.error('Erro ao carregar produtos:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <>
      <Navbar expand="lg" className="bg-body-tertiary">
        <Container>
          <Navbar.Brand href="#home">Lancheirinha da Nutri</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Link</Nav.Link>
              <NavDropdown title="Produtos" id="basic-nav-dropdown">
                <NavDropdown.Item href="#garrafas">Garrafas Térmicas</NavDropdown.Item>
                <NavDropdown.Item href="#vasilhas">Vasilhas, copos e canudos</NavDropdown.Item>
                <NavDropdown.Item href="#talheres">Conjunto de Talheres</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#brinquedos">Brinquedos de cozinha infantil</NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Seção para exibir a lista de produtos */}
      <Container>
        <h1 className="mt-4">Lista de Produtos</h1>
        <div className="row">
          {products.length > 0 ? (
            products.map((product) => (
              <div className="col-md-4 mb-4" key={product.id}>
                <div className="card">
                  {product.image && (
                    <img
                      src={`http://localhost:3000/uploads/${product.image}`}
                      className="card-img-top"
                      alt={product.name}
                    />
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text">{product.description}</p>
                    <p className="card-text"><strong>R$ {product.price.toFixed(2)}</strong></p>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>Carregando produtos...</p>
          )}
        </div>
      </Container>

      {/* Imagem principal */}
      <FluidExample />
    </>
  );
}

function FluidExample() {
  return <Image src={lancheirinha} fluid />;
}

export default App;
